       const xhr = new XMLHttpRequest();
       xhr.open('GET', '/js/i18n/ru-compiled.161baa3becc3bd123445eaa78279f070.json');
       xhr.responseType = 'json';
       xhr.onload = function(e) {
         if (this.status === 200) {
           res({ru: xhr.response});
         } else {
           rej(e);
         }
       };
       xhr.send();
     });

